import { useState } from "react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";
import {createQuestion, createMaterial} from "../models/block";
import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";




function QuestionEditor({ q, onQuestionChange, onQuestionDelete}) {
    //onChange passed down from the parent so everything stays in sync
  const update = (field, value) => {
    //ONCHANGE CREATES A NEW QUESTION OBJECT WITH UQPDATED FIELD  VALUE
    onQuestionChange({ ...q, [field]: value }); //field is the placeholder for any property
    //properties of questionBlock blockType, type, prompt, desc
  };

  //PROMPT TEXT BOX
  return (
    <div className="p-4 border rounded mb-4 bg-white shadow">
      <ReactQuill
        type="text"
        placeholder="Prompt"
        className="w-full border p-2 mb-2"
        value={q.prompt}
        rows={3}
        onChange={(value)=>update("prompt",value)}
        // onChange={(e) => {
        //     //dont use update because it cannot pass an object as is
        //     update("prompt", e.target.value);
        //}}
      />

      {/* <ReactQuill
        placeholder="Description"
        className="w-full border p-2 font-mono mb-2"
        rows="3"
        value={q.desc}
        onChange={(value)=>update("desc",value)}
        //onChange={(e) => update("desc",e.target.value)}
      /> */}

    {/* DISPLAY SUB QUESTIONS */}
        {q.subQuestions && q.subQuestions.length > 0 && (
            <div className="ml-4 border-l-2 pl-2">
                {q.subQuestions.map((sq, i) => (
                    <QuestionEditor
                        key={sq.id}
                        q={sq}
                        onQuestionChange={
                            //pass the updated Sub Q from child to parent in
                            //updatedSubQ
                            updatedSubQ=>{ 
                            const updatedSubs = q.subQuestions.map((sub,idx)=>
                                idx === i ? updatedSubQ : sub
                            );
                             // Call parent's onQuestionChange to update the parent question
                            onQuestionChange({...q, subQuestions:updatedSubs});
                        }}
                        onQuestionDelete={()=>{
                            //filter everything but the q to delete
                            const updatedSubs = q.subQuestions.filter((_,idx)=>idx!==i);
                            onQuestionChange({...q,subQuestions:updatedSubs});
                        }}                        
                    />
                ))}
            </div>
        )}

      <select
        className="border p-2"
        value={q.type}
        onChange={(e) => update("type", e.target.value)}
      >
        <option value="short">Short Answer</option>
        <option value="textarea">Paragraph</option>
        <option value="code">Code Response</option>
      </select>

       <button
            onClick={()=>{
                const nextIndex = (q.subQuestions?.length || 0);
                const nextLetter = String.fromCharCode(97+nextIndex); //97=a
                const newSubQ = createQuestion();
                newSubQ.prompt = `${nextLetter}.`;
                const updatedSubs = [...(q.subQuestions || []),newSubQ];
                onQuestionChange({...q,subQuestions: updatedSubs}); //send to parent updated  q's and subQuestions
            }}
            className="bg-green-600 text-white px-4 py-2 rounded"
        >
            Add Sub Question
        </button>
        <button
            onClick={onQuestionDelete}
            className="bg-red-600 text-white px-2 py-1 rounded ml-2"
        >
            Delete
        </button>

    </div>
  );
}

function MaterialEditor({block, onMaterialChange, onMaterialDelete}){
    const [image, setImage] = useState();
    const update = (field,value) =>{
        //ONCHANGE CREATES A NEW BLOCK OBJECT WITH UPDATED FIELD AND TYPE VALUES 
        onMaterialChange({...block, [field]:value})
        //text image block properties blockType, type, content
    };

    const addImage=(base64)=>{
        onMaterialChange({
            ...block,
            images: [...(block.images||[]),base64]
        })
    };
    return(
        <div className="p-4 border rounded mb-4 bg-white shadow">
            <ReactQuill
                placeholder="Paste image or write here"
                rows = {8}
                className="w-full border p-2 font-mono mb-2"
                value={block.content}
                // onChange handler doesn't receive a DOM event object, gives you content value directly
                onChange={value=>update("content",value)}

                onPaste={async (e)=>{
                    //find item from clipboard that is an image
                    const item = Array.from(e.clipboardData.items).find(i=>i.type.startsWith("image/"));
                    if(item){
                        const file = item.getAsFile(); //ge tthe image file
                        const reader = new FileReader();
                        reader.onload =(ev)=>{ //executes once image file has been converted to base64 data URL
                            addImage(ev.target.result);            
                            //update("content",ev.target.result);
                        }
                        reader.readAsDataURL(file);
                        e.preventDefault();
                    }
                }}
            />
            {/* Render images above Markdown preview */}
            {block.images && block.images.length > 0 && (
                <div className="my-2 flex flex-wrap gap-2">
                    {block.images.map((src, idx) => (
                        <img key={idx} src={src} alt={`Pasted ${idx}`} style={{maxWidth: "100%"}} />
                    ))}
                </div>
            )}

            <div className="mt-2 p-2 border bg-gray-50"
                 dangerouslySetInnerHTML={{ __html: block.content }} />



            <button
            onClick={onMaterialDelete}
            className="bg-red-600 text-white px-2 py-1 rounded ml-2"
            >
                Delete
            </button>
        </div>
    )
}

function LabBuilder(){
    const [blocks, setBlocks] = useState([]); //directions, questions, etc
    const [title, setTitle] = useState("");

    const deleteBlock = (id) =>{
        setBlocks(blocks.filter(b=> b.id !== id)); //remove block with id
    }

    const addMaterialBlock = () => { //type can be text, or image?
        setBlocks([
            ...blocks, 
            createMaterial()
        ]);

    }

    const addQuestionBlock = () => {

        setBlocks([
        ...blocks,
        createQuestion()
        ]);
    };

    //update block from child component 
    const updateBlock = (id, updated) => {
        //replace old block with new block
        setBlocks(blocks.map((b) => (b.id === id ? updated : b)));
    };

    const moveBlock = (from, to)=>{
        if(to<0 || to>blocks.length-1) return;
        const updatedBlocks = [...blocks];
        //remove block is moving
        const [moveBlock] = updatedBlocks.splice(from,1);
        //you could also do moveBlock= and then call moveBlock[0]
        //move moveBlock to "to" index
        updatedBlocks.splice(to,0,moveBlock);
        setBlocks(updatedBlocks);
    }

    const saveLab = () => {
        const lab = { title: title, blocks};
        localStorage.setItem("labData", JSON.stringify(lab));
        console.log("Lab JSON:", lab);
        //alert("Lab saved! Check console for JSON.");
    };

    const loadLab = ()=>{
        const labData = localStorage.getItem("labData");
        if(labData){
            try{
                const lab = JSON.parse(labData);
                setTitle(lab.title || "");
                setBlocks(lab.blocks || []);
                console.log("lab loaded into blocks");
            }catch(e){
                alert("Failed to parse lab JSON!");
            }
        }else{
            alert("No saved lab found");
        }
    }

    return (
        <div className="max-w-3xl mx-auto p-6">
        <h1 className="text-2xl font-bold mb-4" style={{whiteSpace: "pre-line"}}>
            {title}
        </h1>
        <textarea 
            type="text"
            className="w-full border p-3 text-xl font-semibold mb-6"
            placeholder="Enter lab title"
            value={title}
            onChange={(e)=> setTitle(e.target.value)}
            onKeyDown={(e)=>{
                if(e.key=== "Enter"){
                    e.preventDefault();
                    saveLab();
                }
            }}
        />

    {/* DISPLAY BLOCKS */}
        {blocks.map((block, i) => (
            <div key={block.id || i} className="mb-6 flex items-start">
                {/* Arrow buttons on the left */}
                <div className="flex flex-col mr-2">
                    <button
                        disabled={i === 0}
                        onClick={() => moveBlock(i, i - 1)}
                        className="bg-gray-300 text-black px-2 py-1 rounded mb-1"
                        title="Move Up"
                    >
                        ↑
                    </button>
                    <button
                        disabled={i === blocks.length - 1}
                        onClick={() => moveBlock(i, i + 1)}
                        className="bg-gray-300 text-black px-2 py-1 rounded"
                        title="Move Down"
                    >
                        ↓
                    </button>
                </div>
         {/* Block editor */}
                <div className="flex-1">
                    {block.blockType === "material" ? (
                        <MaterialEditor
                            key={block.id}
                            block={block}
                            onMaterialChange={(updatedBlock) => updateBlock(block.id, updatedBlock)}
                            onMaterialDelete={() => deleteBlock(block.id)}
                        />
                    ) : (
                        <QuestionEditor
                            key={block.id}
                            q={block}
                            onQuestionChange={(updatedBlock) => updateBlock(block.id, updatedBlock)}
                            onQuestionDelete={() => deleteBlock(block.id)}
                        />
                    )}
                </div>
            </div>
        ))}

    {/* BUTTONS */}
        <button 
            onClick={addMaterialBlock}
            className="bg-green-600 text-white px-4 py-2 rounded mr-2"
        >
            Add Materials
        </button>
        <button
            onClick={addQuestionBlock}
            className="bg-green-600 text-white px-4 py-2 rounded mr-2"
        >
            ➕ Add Question
        </button>
        <button
            onClick={saveLab}
            className="bg-blue-600 text-white px-4 py-2 rounded"
        >
            💾 Save
        </button>
        <button
            onClick={loadLab}
            className="bg-yellow-600 text-white px-4 py-2 rounded mr-2"
        >
            📂 Load
        </button>
        <button
            onClick={() => {
                const lab = { title, blocks };
                const blob = new Blob([JSON.stringify(lab, null, 2)], { type: "application/json" });
                const url = URL.createObjectURL(blob);
                const a = document.createElement("a");
                a.href = url;
                a.download = "lab.json";
                a.click();
                URL.revokeObjectURL(url);
            }}
            className="bg-blue-600 text-white px-4 py-2 rounded ml-2"
        >
            ⬇️ Export
</button>


    {/* LAB PREVIEW */}
        <div className="mt-8 p-6 border rounded bg-gray-100">
            <h2 className="text-xl font-bold mb-4">Lab Preview</h2>
        </div>
        <div>
            <h3 className="font-semibold mb-2">{title}</h3>
        </div>
            {blocks.map((block, i)=>(
                <div key={block.id || i} className="mb-6">
                    {block.blockType === "material" ? (
                        <> {/*<></> allows you to return multiple elements together*/}
                            {/* Show images */}
                            {block.images && block.images.length > 0 && (
                                <div className="my-2 flex flex-wrap gap-2">
                                    {block.images.map((src, idx) => (
                                        <img key={idx} src={src} alt={`Material ${i}`} style={{maxWidth: "100%"}} />
                                    ))}
                                </div>
                            )}
                            {/* Show reactQuill html  */}
                             <div className="mt-2 p-2 border bg-gray-50"
                                dangerouslySetInnerHTML={{ __html: block.content }} />
                        </>
                    ) : (
                        <>
                            <div>
                                <div className="font-semibold mb-1" dangerouslySetInnerHTML={{ __html: block.content }} />
                                {/* <div className="mb-2 text-gray-700" dangerouslySetInnerHTML={{ __html: block.desc }} /> */}
                                {block.subQuestions.length===0 && (
                                    <>
                                        {block.type === "short" && (<input type="text" className="w-full border p-2 mb-2" placeholder="Your answer..." />)}
                                        {block.type === "textarea" && (<textarea className="w-full border p-2 mb-2" rows={3} placeholder="Your answer..." />)}
                                        {block.type === "code" && (<textarea className="w-full border font-mono p-2 mb-2" rows={6} placeholder="Your code..." />)}
                                    </>
                                    )
                                }
                            </div>
                            {block.subQuestions && block.subQuestions.length > 0 && (
                                <div className="ml-4 border-l-2 pl-2">
                                    {block.subQuestions.map((sq, j) => (
                                        <div key={sq.id || j} className="mb-4">
                                            <div className="font-semibold mb-1" dangerouslySetInnerHTML={{ __html: block.content }} />
                                            {/* <div className="mb-2 text-gray-700" dangerouslySetInnerHTML={{ __html: block.desc }} /> */}
                                            {sq.type === "short" && (
                                                <input type="text" className="w-full border p-2 mb-2" placeholder="Your answer..." />
                                            )}
                                            {sq.type === "textarea" && (
                                                <textarea className="w-full border p-2 mb-2" rows={3} placeholder="Your answer..." />
                                            )}
                                            {sq.type === "code" && (
                                                <textarea className="w-full border font-mono p-2 mb-2" rows={6} placeholder="Your code..." />
                                            )}
                                        </div>
                                    ))}
                                </div>
                            )}
                        </>
                    )}
                </div>
            ))}

        </div>

    );
}

export default  LabBuilder;
