import { useState } from "react";
import ReactMarkdown from "react-markdown";
import remarkGfm from "remark-gfm";

function QuestionEditor({ q, onChange }) {
    //onChange passed down from the parent function 
  const update = (field, value) => {
    onChange({ ...q, [field]: value });
  };

  return (
    <div className="p-4 border rounded mb-4 bg-white shadow">
      <input
        type="text"
        placeholder="Prompt"
        className="w-full border p-2 mb-2"
        value={q.prompt}
        onChange={(e) => update("prompt", e.target.value)}
      />

      <textarea
        placeholder="Optional code snippet"
        className="w-full border p-2 font-mono mb-2"
        rows="3"
        value={q.code}
        onChange={(e) => update("code", e.target.value)}
      />

      <select
        className="border p-2"
        value={q.type}
        onChange={(e) => update("type", e.target.value)}
      >
        <option value="short">Short Answer</option>
        <option value="textarea">Paragraph</option>
        <option value="code">Code Response</option>
      </select>
    </div>
  );
}

function ImageEditor({block, onChange}){
    const [image, setImage] = useState();
    return(
        <div className="p-4 border rounded mb-4 bg-white shadow">
            <textarea
                placeholder="Paste images here"
                rows = "3"
                value={block.content}
                onChange={e=> onChange({...block, content: e.target.value})}
                onPaste={async (e)=>{
                    //find item from clipboard that is an image
                    const item = Array.from(e.clipboardData.items).find(i=>i.type.startsWith("image/"));
                    if(item){
                        const file = item.getAsFile(); //ge tthe image file
                        const url = URL.createObjectURL(file);
                        setImage(url);
                        e.preventDefault();
                    }
                }}
            />
            {image && (
                <div className="my-2">
                    <img src={image} alt="Pasted" style={{maxWidth: "100%"}} />
                    <div className="text-xs text-gray-500">Image preview (not saved in Markdown)</div>
                </div>
                )}
        </div>
    )
}

function LabBuilder(){
    const [questions, setQuestions] = useState([]);
    const [title, setTitle] = useState([]);

    const addTextImageBlock = () => {
        setQuestions([
            ...questions, {id: Date.now(), type: "text", content: ""}
        ]);
    }

    const addQuestion = () => {
        setQuestions([
        ...questions,
        { id: Date.now(), prompt: "", code: "", type: "question" }
        ]);
    };

    const updateQuestion = (id, updated) => {
        setQuestions(questions.map((q) => (q.id === id ? updated : q)));
    };

    const saveLab = () => {
        const lab = { title: title, questions};
        console.log("Lab JSON:", lab);
        alert("Lab saved! Check console for JSON.");
    };

    return (
        <div className="max-w-3xl mx-auto p-6">
        <h1 className="text-2xl font-bold mb-4">{title}</h1>
        <input 
            type="text"
            className="w-full border p-3 text-xl font-semibold mb-6"
            placdeholder="Enter lab title"
            value={title}
            onChange={(e)=> setTitle(e.target.value)}
            onKeyDown={(e)=>{
                if(e.key=== "Enter"){
                    e.preventDefault();
                    saveLab();
                }
            }}
        />

        {questions.map((block) => (
            block.type === "text" ? 
            (
                <ImageEditor
                key={block.id}
                block={block}
                onChange={(updated) => updateQuestion(block.id, updated)}
                />
            ) : (
                <QuestionEditor
                key={block.id}
                q={block}
                onChange={(updated) => updateQuestion(block.id, updated)}
                />
            )
        ))}
        <button 
            onClick={addTextImageBlock}
            className="bg-green-600 text-white px-4 py-2 rounded mr-2"
        >
            Add text/image ðŸ“· 
        </button>
        <button
            onClick={addQuestion}
            className="bg-green-600 text-white px-4 py-2 rounded mr-2"
        >
            âž• Add Question
        </button>
        <button
            onClick={saveLab}
            className="bg-blue-600 text-white px-4 py-2 rounded"
        >
            ðŸ’¾ Save Lab
        </button>
        </div>
    );
}

export default  LabBuilder;
